fragment:

        https://#keyVaultHost#/secrets/snl-client-id/?api-version=7.0
        GET


    <set-variable name="snlClient" value="@{
								var secret = ((IResponse)context.Variables["snlClientResponse"]).Body.As();
								return secret["value"].ToString();
						}">


        https://#keyVaultHost#/secrets/snl-client-pwd/?api-version=7.0
        GET


    <set-variable name="snlSecret" value="@{
                                    var secret = ((IResponse)context.Variables["snlSecretResponse"]).Body.As();
                                    return secret["value"].ToString();
                            }">


        #sAndLOauthUrl#
        POST

            application/x-www-form-urlencoded

        @{
            return "username=" + (string)context.Variables["snlClient"] + "&password=" + (string)context.Variables["snlSecret"] + "&grant_type=password_credentials";}
